mapper.select:
SELECT id,name,image,test_desc AS testDesc,topic_group AS topicGroup
FROM mendao_test
WHERE id=#{id}

mapper.update:
UPDATE mendao_test
set name=#{mendaoTest.name},
    image=#{mendaoTest.image},
    test_desc=#{mendaoTest.testDesc},
    topic_group=#{mendaoTest.topicGroup}
WHERE id = #{mendaoTest.id}
